    $$
    {\Delta _{RGB}} ={\sqrt {(R_{dist}-R_{tar})^{2}+(G_{dist}-G_{tar})^{2}+(B_{dist}-B_{tar})^{2}}}
    $$

    $$
    \begin{align}
    D(RGB_{dist},RGB_{tar})
    &amp;=  \frac{\vert R_{dist}-R_{tar} \vert + \vert G_{dist}-G_{tar} \vert + \vert B_{dist}-B_{tar} \vert}{255}

    &amp;=
    \end{align}
    $$

    $$
    \alpha_{mixed} = 1 - (1-\alpha_1) \times (1-\alpha_2) = 0.4
    $$

    $$
    y_1 = +(256 \times 3 \times 0.75) = +576
    $$
    $$
    y_2 = -(256 \times 3 \times 0.1) = -76.8
    $$

    $$
    Y_{mixed} = Y(y_1 \times \alpha_{mixed} + (-y_2)) = 0.53
    $$

    $$
    B1 = \frac{R}{R+G+B} = 0 ,\ B2 = 1- \frac{B}{R+G+B}=1
    $$

    $$
    Y(RGB) = 1.0000 \times R +4.5907 \times G + 0.0601 \times B
    $$

    $$
    RGB_{dist}(YBa) = Y(Yâ€™(Y_1) + Yâ€™(Y_2)) \times mixbox((B_{11},B_{12}),(B_{21},B_{22})) \times (1-\alpha_1 \alpha_2)
    $$

    $$
    a(a_1,a_2) = 1 - (1-a_1) \times (1-a_2)
    $$

    $$
    y = \frac{6v&#39;}{4u&#39; - 2v&#39; + 12}
    $$
