&lt;h2 id=&#34;тЪ║ТюгжЌ«жбў&#34;&gt;тЪ║ТюгжЌ«жбў&lt;/h2&gt;
&lt;h3 id=&#34;тЏътйњжЌ«жбў&#34;&gt;тЏътйњжЌ«жбў&lt;/h3&gt;
&lt;h4 id=&#34;у║┐ТђДтЏътйњ&#34;&gt;у║┐ТђДтЏътйњ&lt;/h4&gt;
&lt;figure class=&#34;highlight python&#34;&gt;&lt;table&gt;&lt;tr&gt;&lt;td class=&#34;code&#34;&gt;&lt;pre&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;keyword&#34;&gt;import&lt;/span&gt; numpy &lt;span class=&#34;keyword&#34;&gt;as&lt;/span&gt; np&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;keyword&#34;&gt;import&lt;/span&gt; matplotlib.pyplot &lt;span class=&#34;keyword&#34;&gt;as&lt;/span&gt; mp&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;keyword&#34;&gt;from&lt;/span&gt; mpl_toolkits.mplot3d &lt;span class=&#34;keyword&#34;&gt;import&lt;/span&gt; axes3d&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;keyword&#34;&gt;import&lt;/span&gt; sklearn.preprocessing &lt;span class=&#34;keyword&#34;&gt;as&lt;/span&gt; sp&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;comment&#34;&gt;# У«Гу╗ЃТЋ░ТЇ«жЏє&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;train_x = np.array([&lt;span class=&#34;number&#34;&gt;0.5&lt;/span&gt;, &lt;span class=&#34;number&#34;&gt;0.6&lt;/span&gt;, &lt;span class=&#34;number&#34;&gt;0.8&lt;/span&gt;, &lt;span class=&#34;number&#34;&gt;1.1&lt;/span&gt;, &lt;span class=&#34;number&#34;&gt;1.4&lt;/span&gt;])  &lt;span class=&#34;comment&#34;&gt;# УЙЊтЁЦжЏє&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;train_y = np.array([&lt;span class=&#34;number&#34;&gt;5.0&lt;/span&gt;, &lt;span class=&#34;number&#34;&gt;5.5&lt;/span&gt;, &lt;span class=&#34;number&#34;&gt;6.0&lt;/span&gt;, &lt;span class=&#34;number&#34;&gt;6.8&lt;/span&gt;, &lt;span class=&#34;number&#34;&gt;7.0&lt;/span&gt;])  &lt;span class=&#34;comment&#34;&gt;# УЙЊтЄ║жЏє&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;n_epochs = &lt;span class=&#34;number&#34;&gt;30&lt;/span&gt;  &lt;span class=&#34;comment&#34;&gt;# У┐ГС╗БТгАТЋ░&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;l_rate = &lt;span class=&#34;number&#34;&gt;0.01&lt;/span&gt;  &lt;span class=&#34;comment&#34;&gt;# тГдС╣аујЄ&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;epochs = []  &lt;span class=&#34;comment&#34;&gt;# У«░тйЋУ┐ГС╗БТгАТЋ░&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;losses = []  &lt;span class=&#34;comment&#34;&gt;# У«░тйЋТЇЪтц▒тђ╝&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;w0, w1 = [&lt;span class=&#34;number&#34;&gt;1&lt;/span&gt;], [&lt;span class=&#34;number&#34;&gt;1&lt;/span&gt;]  &lt;span class=&#34;comment&#34;&gt;# ТеАтъІтѕЮтДІтђ╝&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;keyword&#34;&gt;for&lt;/span&gt; i &lt;span class=&#34;keyword&#34;&gt;in&lt;/span&gt; &lt;span class=&#34;built_in&#34;&gt;range&lt;/span&gt;(&lt;span class=&#34;number&#34;&gt;1&lt;/span&gt;, n_epochs + &lt;span class=&#34;number&#34;&gt;1&lt;/span&gt;):&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    epochs.append(i)  &lt;span class=&#34;comment&#34;&gt;# У«░тйЋуггтЄаТгАУ┐ГС╗Б&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    y = w0[-&lt;span class=&#34;number&#34;&gt;1&lt;/span&gt;] + w1[-&lt;span class=&#34;number&#34;&gt;1&lt;/span&gt;] * train_x  &lt;span class=&#34;comment&#34;&gt;# тЈќтЄ║ТюђТќ░уџёw0,w1У«Ау«Ќу║┐ТђДТќ╣уеІУЙЊтЄ║&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    &lt;span class=&#34;comment&#34;&gt;# ТЇЪтц▒тЄйТЋ░(тЮЄТќ╣ти«)&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    loss = (((train_y - y)**&lt;span class=&#34;number&#34;&gt;2&lt;/span&gt;).&lt;span class=&#34;built_in&#34;&gt;sum&lt;/span&gt;()) / &lt;span class=&#34;number&#34;&gt;2&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    losses.append(loss)  &lt;span class=&#34;comment&#34;&gt;# У«░тйЋТ»ЈТгАУ┐ГС╗БуџёТЇЪтц▒тђ╝&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    &lt;span class=&#34;built_in&#34;&gt;print&lt;/span&gt;(&lt;span class=&#34;string&#34;&gt;&amp;quot;%d: w0=%f, w1=%f, loss=%f&amp;quot;&lt;/span&gt; % (i, w0[-&lt;span class=&#34;number&#34;&gt;1&lt;/span&gt;], w1[-&lt;span class=&#34;number&#34;&gt;1&lt;/span&gt;], loss))&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    &lt;span class=&#34;comment&#34;&gt;# У«Ау«Ќw0,w1уџётЂЈт»╝ТЋ░&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    d0 = -(train_y - y).&lt;span class=&#34;built_in&#34;&gt;sum&lt;/span&gt;()&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    d1 = -(train_x * (train_y - y)).&lt;span class=&#34;built_in&#34;&gt;sum&lt;/span&gt;()&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    &lt;span class=&#34;comment&#34;&gt;# ТЏ┤Тќ░w0,w1&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    w0.append(w0[-&lt;span class=&#34;number&#34;&gt;1&lt;/span&gt;] - (d0 * l_rate))&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    w1.append(w1[-&lt;span class=&#34;number&#34;&gt;1&lt;/span&gt;] - (d1 * l_rate))&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;comment&#34;&gt;###################### У«Гу╗ЃУ┐ЄуеІтЈ»УДєтїќ ######################&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;comment&#34;&gt;## ТЇЪтц▒тЄйТЋ░ТћХТЋЏУ┐ЄуеІ&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;w0 = np.array(w0[:-&lt;span class=&#34;number&#34;&gt;1&lt;/span&gt;])&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;w1 = np.array(w1[:-&lt;span class=&#34;number&#34;&gt;1&lt;/span&gt;])&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;mp.figure(&lt;span class=&#34;string&#34;&gt;&amp;quot;Losses&amp;quot;&lt;/span&gt;, facecolor=&lt;span class=&#34;string&#34;&gt;&amp;quot;lightgray&amp;quot;&lt;/span&gt;)  &lt;span class=&#34;comment&#34;&gt;# тѕЏт╗║СИђСИфуфЌСйЊ&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;mp.title(&lt;span class=&#34;string&#34;&gt;&amp;quot;epoch&amp;quot;&lt;/span&gt;, fontsize=&lt;span class=&#34;number&#34;&gt;20&lt;/span&gt;)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;mp.ylabel(&lt;span class=&#34;string&#34;&gt;&amp;quot;loss&amp;quot;&lt;/span&gt;, fontsize=&lt;span class=&#34;number&#34;&gt;14&lt;/span&gt;)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;mp.grid(linestyle=&lt;span class=&#34;string&#34;&gt;&amp;quot;:&amp;quot;&lt;/span&gt;)  &lt;span class=&#34;comment&#34;&gt;# уйЉТа╝у║┐№╝џУЎџу║┐&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;mp.plot(epochs, losses, c=&lt;span class=&#34;string&#34;&gt;&amp;quot;blue&amp;quot;&lt;/span&gt;, label=&lt;span class=&#34;string&#34;&gt;&amp;quot;loss&amp;quot;&lt;/span&gt;)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;mp.legend()  &lt;span class=&#34;comment&#34;&gt;# тЏЙСЙІ&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;mp.tight_layout()  &lt;span class=&#34;comment&#34;&gt;# у┤ДтЄЉТа╝т╝Ј&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;comment&#34;&gt;## ТўЙуц║ТеАтъІуЏ┤у║┐&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;pred_y = w0[-&lt;span class=&#34;number&#34;&gt;1&lt;/span&gt;] + w1[-&lt;span class=&#34;number&#34;&gt;1&lt;/span&gt;] * train_x  &lt;span class=&#