br&gt;&lt;span class=&#34;line&#34;&gt;        plt.scatter(data[:, &lt;span class=&#34;number&#34;&gt;0&lt;/span&gt;], data[:, &lt;span class=&#34;number&#34;&gt;1&lt;/span&gt;], c=y, s=&lt;span class=&#34;number&#34;&gt;12&lt;/span&gt;, edgecolors=&lt;span class=&#34;string&#34;&gt;&amp;#x27;k&amp;#x27;&lt;/span&gt;, cmap=cm)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        plt.title(&lt;span class=&#34;string&#34;&gt;&amp;#x27;Prime&amp;#x27;&lt;/span&gt;, fontsize=&lt;span class=&#34;number&#34;&gt;12&lt;/span&gt;)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        plt.grid(b=&lt;span class=&#34;literal&#34;&gt;True&lt;/span&gt;, ls=&lt;span class=&#34;string&#34;&gt;&amp;#x27;:&amp;#x27;&lt;/span&gt;)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        data_min1, data_min2 = np.&lt;span class=&#34;built_in&#34;&gt;min&lt;/span&gt;(data, axis=&lt;span class=&#34;number&#34;&gt;0&lt;/span&gt;)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        data_max1, data_max2 = np.&lt;span class=&#34;built_in&#34;&gt;max&lt;/span&gt;(data, axis=&lt;span class=&#34;number&#34;&gt;0&lt;/span&gt;)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        plt.xlim(expand(data_min1, data_max1, &lt;span class=&#34;number&#34;&gt;0.05&lt;/span&gt;))&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        plt.ylim(expand(data_min2, data_max2, &lt;span class=&#34;number&#34;&gt;0.05&lt;/span&gt;))&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        connectivity = kneighbors_graph(data,&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                                        n_neighbors=&lt;span class=&#34;number&#34;&gt;7&lt;/span&gt;,&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                                        mode=&lt;span class=&#34;string&#34;&gt;&amp;#x27;distance&amp;#x27;&lt;/span&gt;,&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                                        metric=&lt;span class=&#34;string&#34;&gt;&amp;#x27;minkowski&amp;#x27;&lt;/span&gt;,&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                                        p=&lt;span class=&#34;number&#34;&gt;2&lt;/span&gt;,&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                                        include_self=&lt;span class=&#34;literal&#34;&gt;True&lt;/span&gt;)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        connectivity = &lt;span class=&#34;number&#34;&gt;0.5&lt;/span&gt; * (connectivity + connectivity.T)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        &lt;span class=&#34;keyword&#34;&gt;for&lt;/span&gt; i, linkage &lt;span class=&#34;keyword&#34;&gt;in&lt;/span&gt; &lt;span class=&#34;built_in&#34;&gt;enumerate&lt;/span&gt;(linkages):&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            ac = AgglomerativeClustering(n_clusters=n_clusters,&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                                         affinity=&lt;span class=&#34;string&#34;&gt;&amp;#x27;euclidean&amp;#x27;&lt;/span&gt;,&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                                         connectivity=connectivity,&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                                         linkage=linkage)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            ac.fit(data)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            y = ac.labels_&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            plt.subplot(&lt;span class=&#34;number&#34;&gt;4&lt;/span&gt;, &lt;span class=&#34;number&#34;&gt;4&lt;/span&gt;, i + &lt;span class=&#34;number&#34;&gt;2&lt;/span&gt; + &lt;span class=&#34;number&#34;&gt;4&lt;/span&gt; * index)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            plt.scatter(data[:, &lt;span class=&#34;number&#34;&gt;0&lt;/span&gt;],&lt