        class_names=iris_class,&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                                filled=&lt;span class=&#34;literal&#34;&gt;True&lt;/span&gt;,&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                                rounded=&lt;span class=&#34;literal&#34;&gt;True&lt;/span&gt;,&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                                special_characters=&lt;span class=&#34;literal&#34;&gt;True&lt;/span&gt;)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;graph = pydotplus.graph_from_dot_data(dot_data)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;graph.write_pdf(&lt;span class=&#34;string&#34;&gt;&amp;#x27;./_data_set/iris_classification/iris.pdf&amp;#x27;&lt;/span&gt;)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;f = &lt;span class=&#34;built_in&#34;&gt;open&lt;/span&gt;(&lt;span class=&#34;string&#34;&gt;&amp;#x27;./_data_set/iris_classification/iris.png&amp;#x27;&lt;/span&gt;, &lt;span class=&#34;string&#34;&gt;&amp;#x27;wb&amp;#x27;&lt;/span&gt;)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;f.write(graph.create_png())&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;f.close()&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;comment&#34;&gt;# 画图&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;N, M = &lt;span class=&#34;number&#34;&gt;50&lt;/span&gt;, &lt;span class=&#34;number&#34;&gt;50&lt;/span&gt;  &lt;span class=&#34;comment&#34;&gt;# 横纵各采样多少个值&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;x1_min, x2_min = x.&lt;span class=&#34;built_in&#34;&gt;min&lt;/span&gt;()&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;x1_max, x2_max = x.&lt;span class=&#34;built_in&#34;&gt;max&lt;/span&gt;()&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;t1 = np.linspace(x1_min, x1_max, N)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;t2 = np.linspace(x2_min, x2_max, M)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;x1, x2 = np.meshgrid(t1, t2)  &lt;span class=&#34;comment&#34;&gt;# 生成网格采样点&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;x_show = np.stack((x1.flat, x2.flat), axis=&lt;span class=&#34;number&#34;&gt;1&lt;/span&gt;)  &lt;span class=&#34;comment&#34;&gt;# 测试点&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;built_in&#34;&gt;print&lt;/span&gt;(x_show.shape)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;built_in&#34;&gt;print&lt;/span&gt;(&lt;span class=&#34;string&#34;&gt;&amp;#x27;x_show = \n&amp;#x27;&lt;/span&gt;, x_show)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;cm_light = mpl.colors.ListedColormap([&lt;span class=&#34;string&#34;&gt;&amp;#x27;#A0FFA0&amp;#x27;&lt;/span&gt;, &lt;span class=&#34;string&#34;&gt;&amp;#x27;#FFA0A0&amp;#x27;&lt;/span&gt;, &lt;span class=&#34;string&#34;&gt;&amp;#x27;#A0A0FF&amp;#x27;&lt;/span&gt;])&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;cm_dark = mpl.colors.ListedColormap([&lt;span class=&#34;string&#34;&gt;&amp;#x27;g&amp;#x27;&lt;/span&gt;, &lt;span class=&#34;string&#34;&gt;&amp;#x27;r&amp;#x27;&lt;/span&gt;, &lt;span class=&#34;string&#34;&gt;&amp;#x27;b&amp;#x27;&lt;/span&gt;])&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;y_show_hat = model.predict(x_show)  &lt;span class=&#34;comment&#34;&gt;# 预测值&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;built_in&#34;&gt;print&lt;/span&gt;(y_show_hat.shape)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;built_in&#34;&gt;print&lt;/span&gt;(y_show_hat)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;y_show_hat = y_show_hat.reshape(x1.shape)  &lt;span class=&#34;comment&#34;&gt;# 使之与输入的形状相同&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;built_in&#34;&gt;print&lt;/span&gt;(y_show_hat)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;plt.figure(facecolor=&lt;span class=&#34;string&#34;&gt;&amp;#x27;w&amp;#x27;&lt;/span&gt;)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;plt.pcolormesh(x1, x2, y_show_hat, cmap=cm_light, shading=&lt;span class=&#34;string&#34;&gt;&amp;#x27;auto&amp;#x27;&lt;/span&gt;)  &lt;span class=&#34;comment&#34;&gt;# 预测值的显示&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;plt.scatter(x_test[&lt;span class=&#34;number&#34;&gt;0&lt;/span&gt;],&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            x_test[&lt;span class=&#34;number&#34;&gt;1&lt;/span&gt;],&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            c=y_test.ravel(),&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            edgecolors=&lt;span class=&#34;string&#34;&gt;&amp;#x27;k&amp;#x27;&lt;/span&gt;,&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            s=&lt;span class=&#34;number&#34;&gt;100&lt;/span&gt;,&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            zorder=&lt;span class=&#34;number&#34;&gt;10&lt;/span&gt;,&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            cmap=cm_dark,&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            marker=&lt;span class=&#34;string&#34;&gt;&amp;#x27;*&amp;#x27;&lt;/span&gt;)  &lt;span class=&#34;comment&#34;&gt;# 测试数据&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;plt.scatter(x[&lt;span class=&#34;number&#34;&gt;0&lt;/span&gt;], x[&lt;span class=&#34;number&#34;&gt;1&lt;/span&gt;], c=y.ravel(), edgecolors=&lt;span class=&#34;string&#34;&gt;&amp;#x27;k&amp;#x27;&lt;/span&gt;, s=&lt;span class=&#34;number&#34;&gt;20&lt;/span&gt;,&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            cmap=cm_dark)  &lt;span class=&#34;comment&#34;&gt;# 全部数据&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;plt.xlabel(iris_feature[&lt;span class=&#34;number&#34;&gt;0&lt;/span&gt;], fontsize=&lt;span class=&#34;number&#34;&gt;13&lt;/span&gt;)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;plt.ylabel(iris_feature[&lt;span class=&#34;number&#34;&gt;1&lt;/span&gt;], fontsize=&lt;span class=&#34;number&#34;&gt;13&lt;/span&gt;)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;plt.xlim(x1_min, x1_max)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;plt.ylim(x2_min, x2_max)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;plt.grid(b=&lt;span class=&#34;literal&#34;&gt;True&lt;/span&gt;, ls=&lt;span class=&#34;string&#34;&gt;&amp;#x27;:&amp;#x27;&lt;/span&gt;, color=&lt;span class=&#34;string&#34;&gt;&amp;#x27;#606060&amp;#x27;&lt;/span&gt;)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;plt.title(&lt;span class=&#34;string&#34;&gt;&amp;#x27;鸢尾花数据的决策树分类&amp;#x27;&lt;/span&gt;, fontsize=&lt;span class=&#34;number&#34;&gt;15&lt;/span&gt;)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;plt.show()&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;comment&#34;&gt;# 训练集上的预测结果&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;y_test = y_test.reshape(-&lt;span class=&#34;number&#34;&gt;1&lt;/span&gt;)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;built_in&#34;&gt;print&lt;/span&gt;(y_test_hat)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;built_in&#34;&gt;print&lt;/span&gt;(y_test)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;result = (y_test_hat == y_test)  &lt;span class=&#34;comment&#34;&gt;# True则预测正确，False则预测错误&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;acc = np.mean(result)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;built_in&#34;&gt;print&lt;/span&gt;(&lt;span class=&#34;string&#34;&gt;&amp;#x27;准确度: %.2f%%&amp;#x27;&lt;/span&gt; % (&lt;span class=&#34;number&#34;&gt;100&lt;/span&gt; * acc))&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;comment&#34;&gt;# 过拟合：错误率&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;depth = np.arange(&lt;span class=&#34;number&#34;&gt;1&lt;/span&gt;, &lt;span class=&#34;number&#34;&gt;15&lt;/span&gt;)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;err_train_list = []&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;err_test_list = []&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;clf = DecisionTreeClassifier(criterion=&lt;span class=&#34;string&#34;&gt;&amp;#x27;entropy&amp;#x27;&lt;/span&gt;)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;keyword&#34;&gt;for&lt;/span&gt; d &lt;span class=&#34;keyword&#34;&gt;in&lt;/span&gt; depth:&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    clf.set_params(max_depth=d)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    clf.fit(x_train, y_train)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    y_train_pred = clf.predict(x_train)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    err_train = &lt;span class=&#34;number&#34;&gt;1&lt;/span&gt; - accuracy_score(y_train, y_train_pred)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    err_train_list.append(err_train)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    y_test_pred = clf.predict(x_test)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    err_test = &lt;span class=&#34;number&#34;&gt;1&lt;/span&gt; - accuracy_score(y_test, y_test_pred)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    err_test_list.append(err_test)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    &lt;span class=&#34;built_in&#34;&gt;print&lt;/span&gt;(d, &lt;span class=&#34;string&#34;&gt;&amp;#x27; 测试集错误率: %.2f%%&amp;#x27;&lt;/span&gt; % (&lt;span class=&#34;number&#34;&gt;100&lt;/span&gt; * err_test))&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;plt.figure(facecolor=&lt;span class=&#34;string&#34;&gt;&amp;#x27;w&amp;#x27;&lt;/span&gt;)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;plt.plot(depth,&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;         err_test_list,&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;         &lt;span class=&#34;string&#34;&gt;&amp;#x27;ro-&amp;#x27;&lt;/span&gt;,&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;         markeredgecolor=&lt;span class=&#34;string&#34;&gt;&amp;#x27;k&amp;#x27;&lt;/span&gt;,&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;         lw=&lt;span class=&#34;number&#34;&gt;2&lt;/span&gt;,&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;         label=&lt;span class=&#34;string&#34;&gt;&amp;#x27;测试集错误率&amp;#x27;&lt;/span&gt;)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;plt.plot(depth,&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;         err_train_list,&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;         &lt;span class=&#34;string&#34;&gt;&amp;#x27;go-&amp;#x27;&lt;/span&gt;,&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;         markeredgecolor=&lt;span class=&#34;string&#34;&gt;&amp;#x27;k&amp;#x27;&lt;/span&gt;,&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;         lw=&lt;span class=&#34;number&#34;&gt;2&lt;/span&gt;,&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;         label=&lt;span class=&#34;string&#34;&gt;&amp;#x27;训练集错误率&amp;#x27;&lt;/span&gt;)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;plt.xlabel(&lt;span class=&#34;string&#34;&gt;&amp;#x27;决策树深度&amp;#x27;&lt;/span&gt;, fontsize=&lt;span class=&#34;number&#34;&gt;13&lt;/span&gt;)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;plt.ylabel(&lt;span class=&#34;string&#34;&gt;&amp;#x27;错误率&amp;#x27;&lt;/span&gt;, fontsize=&lt;span class=&#34;number&#34;&gt;13&lt;/span&gt;)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;plt.legend(loc=&lt;span class=&#34;string&#34;&gt;&amp;#x27;lower left&amp;#x27;&lt;/span&gt;, fontsize=&lt;span class=&#34;number&#34;&gt;13&lt;/span&gt;)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;plt.title(&lt;span class=&#34;string&#34;&gt;&amp;#x27;决策树深度与过拟合&amp;#x27;&lt;/span&gt;, fontsize=&lt;span class=&#34;number&#34;&gt;15&lt;/span&gt;)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;plt.grid(b=&lt;span class=&#34;literal&#34;&gt;True&lt;/span&gt;, ls=&lt;span class=&#34;string&#34;&gt;&amp;#x27;:&amp;#x27;&lt;/span&gt;, color=&lt;span class=&#34;string&#34;&gt;&amp;#x27;#606060&amp;#x27;&lt;/span&gt;)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;plt.show()&lt;/span&gt;&lt;br&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;/figure&gt;
