lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;keyword&#34;&gt;from&lt;/span&gt; sklearn.metrics &lt;span class=&#34;keyword&#34;&gt;import&lt;/span&gt; accuracy_score&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;keyword&#34;&gt;from&lt;/span&gt; sklearn.model_selection &lt;span class=&#34;keyword&#34;&gt;import&lt;/span&gt; train_test_split&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;mpl.rcParams[&lt;span class=&#34;string&#34;&gt;&amp;#x27;font.sans-serif&amp;#x27;&lt;/span&gt;] = [&lt;span class=&#34;string&#34;&gt;&amp;#x27;SimHei&amp;#x27;&lt;/span&gt;]&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;mpl.rcParams[&lt;span class=&#34;string&#34;&gt;&amp;#x27;axes.unicode_minus&amp;#x27;&lt;/span&gt;] = &lt;span class=&#34;literal&#34;&gt;False&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;iris_feature = &lt;span class=&#34;string&#34;&gt;u&amp;#x27;花萼长度&amp;#x27;&lt;/span&gt;, &lt;span class=&#34;string&#34;&gt;u&amp;#x27;花萼宽度&amp;#x27;&lt;/span&gt;, &lt;span class=&#34;string&#34;&gt;u&amp;#x27;花瓣长度&amp;#x27;&lt;/span&gt;, &lt;span class=&#34;string&#34;&gt;u&amp;#x27;花瓣宽度&amp;#x27;&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;path = &lt;span class=&#34;string&#34;&gt;&amp;#x27;iris_classification/iris.data&amp;#x27;&lt;/span&gt;  &lt;span class=&#34;comment&#34;&gt;# 数据文件路径&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;data = pd.read_csv(path, header=&lt;span class=&#34;literal&#34;&gt;None&lt;/span&gt;)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;x_prime = data[&lt;span class=&#34;built_in&#34;&gt;list&lt;/span&gt;(&lt;span class=&#34;built_in&#34;&gt;range&lt;/span&gt;(&lt;span class=&#34;number&#34;&gt;4&lt;/span&gt;))]&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;y = pd.Categorical(data[&lt;span class=&#34;number&#34;&gt;4&lt;/span&gt;]).codes&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;x_prime_train, x_prime_test, y_train, y_test = train_test_split(&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    x_prime, y, train_size=&lt;span class=&#34;number&#34;&gt;0.7&lt;/span&gt;, random_state=&lt;span class=&#34;number&#34;&gt;0&lt;/span&gt;)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;feature_pairs = [[&lt;span class=&#34;number&#34;&gt;0&lt;/span&gt;, &lt;span class=&#34;number&#34;&gt;1&lt;/span&gt;], [&lt;span class=&#34;number&#34;&gt;0&lt;/span&gt;, &lt;span class=&#34;number&#34;&gt;2&lt;/span&gt;], [&lt;span class=&#34;number&#34;&gt;0&lt;/span&gt;, &lt;span class=&#34;number&#34;&gt;3&lt;/span&gt;], [&lt;span class=&#34;number&#34;&gt;1&lt;/span&gt;, &lt;span class=&#34;number&#34;&gt;2&lt;/span&gt;], [&lt;span class=&#34;number&#34;&gt;1&lt;/span&gt;, &lt;span class=&#34;number&#34;&gt;3&lt;/span&gt;], [&lt;span class=&#34;number&#34;&gt;2&lt;/span&gt;, &lt;span class=&#34;number&#34;&gt;3&lt;/span&gt;]]&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;plt.figure(figsize=(&lt;span class=&#34;number&#34;&gt;8&lt;/span&gt;, &lt;span class=&#34;number&#34;&gt;6&lt;/span&gt;), facecolor=&lt;span class=&#34;string&#34;&gt;&amp;#x27;#FFFFFF&amp;#x27;&lt;/span&gt;)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;keyword&#34;&gt;for&lt;/span&gt; i, pair &lt;span class=&#34;keyword&#34;&gt;in&lt;/span&gt; &lt;span class=&#34;built_in&#34;&gt;enumerate&lt;/span&gt;(feature_pairs):&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    &lt;span class=&#34;comment&#34;&gt;# 准备数据&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    x_train = x_prime_train[pair]&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    x_test = x_prime_test[pair]&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    &lt;span class=&#34;comment&#34;&gt;# 决策树学习&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    model = DecisionTreeClassifier(criterion=&lt;span class=&#34;string&#34;&gt;&amp;#x27;entropy&amp;#x27;&lt;/span&gt;, min_samples_leaf=&lt;span class=&#34;number&#34;&gt;3&lt;/span&gt;)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    model.fit(x_train, y_train)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    N, M = &lt;span class=&#34;number&#34;&gt;500&lt;/span&gt;, &lt;span class=&#34;number&#34;&gt;500&lt;/span&gt;  &lt;span class=&#34;comment&#34;&gt;# 横纵各采样多少个值&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    x1_min, x2_min = x_train.&lt;span class=&#34;built_in&#34;&gt;min&lt;/span&gt;()&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    x1_max, x2_max = x_train.&lt;span class=&#34;built_in&#34;&gt;max&lt;/span&gt;()&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    t1 = np.linspace(x1_min, x1_max, N)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    t2 = np.linspace(x2_min, x2_max, M)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    x1, x2 = np.meshgrid(t1, t2)  &lt;span class=&#34;comment&#34;&gt;# 生成网格采样点&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    x_show = np.stack((x1.flat, x2.flat), axis=&lt;span class=&#34;number&#34;&gt;1&lt;/span&gt;)  &lt;span class=&#34;comment&#34;&gt;# 测试点&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    &lt;span class=&#34;comment&#34;&gt;# 训练集上的预测结果&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    y_train_pred = model.predict(x_train)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    acc_train = accuracy_score(y_train, y_train_pred)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    y_test_pred = model.predict(x_test)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    acc_test = accuracy_score(y_test, y_test_pred)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    &lt;span class=&#34;built_in&#34;&gt;print&lt;/span&gt;(&lt;span class=&#34;string&#34;&gt;&amp;#x27;特征：&amp;#x27;&lt;/span&gt;, iris_feature[pair[&lt;span class=&#34;number&#34;&gt;0&lt;/span&gt;]], &lt;span class=&#34;string&#34;&gt;&amp;#x27; + &amp;#x27;&lt;/span&gt;, iris_feature[pair[&lt;span class=&#34;number&#34;&gt;1&lt;/span&gt;]])&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    &lt;span class=&#34;built_in&#34;&gt;print&lt;/span&gt;(&lt;span class=&#34;string&#34;&gt;&amp;#x27;\t训练集准确率: %.4f%%&amp;#x27;&lt;/span&gt; % (&lt;span class=&#34;number&#34;&gt;100&lt;/span&gt; * acc_train))&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    &lt;span class=&#34;built_in&#34;&gt;print&lt;/span&gt;(&lt;span class=&#34;string&#34;&gt;&amp;#x27;\t测试集准确率: %.4f%%\n&amp;#x27;&lt;/span&gt; % (&lt;span class=&#34;number&#34;&gt;100&lt;/span&gt; * acc_test))&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    cm_light = mpl.colors.ListedColormap([&lt;span class=&#34;string&#34;&gt;&amp;#x27;#A0FFA0&amp;#x27;&lt;/span&gt;, &lt;span class=&#34;string&#34;&gt;&amp;#x27;#FFA0A0&amp;#x27;&lt;/span&gt;, &lt;span class=&#34;string&#34;&gt;&amp;#x27;#A0A0FF&amp;#x27;&lt;/span&gt;])&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    cm_dark = mpl.colors.ListedColormap([&lt;span class=&#34;string&#34;&gt;&amp;#x27;g&amp;#x27;&lt;/span&gt;, &lt;span class=&#34;string&#34;&gt;&amp;#x27;r&amp;#x27;&lt;/span&gt;, &lt;span class=&#34;string&#34;&gt;&amp;#x27;b&amp;#x27;&lt;/span&gt;])&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    y_hat = model.predict(x_show)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    y_hat = y_hat.reshape(x1.shape)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    plt.subplot(&lt;span class=&#34;number&#34;&gt;2&lt;/span&gt;, &lt;span class=&#34;number&#34;&gt;3&lt;/span&gt;, i + &lt;span class=&#34;number&#34;&gt;1&lt;/span&gt;)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    plt.contour(x1,&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                x2,&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                y_hat,&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                colors=&lt;span class=&#34;string&#34;&gt;&amp;#x27;k&amp;#x27;&lt;/span&gt;,&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                levels=[&lt;span class=&#34;number&#34;&gt;0&lt;/span&gt;, &lt;span class=&#34;number&#34;&gt;1&lt;/span&gt;],&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                antialiased=&lt;span class=&#34;literal&#34;&gt;True&lt;/span&gt;,&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                linewidths=&lt;span class=&#34;number&#34;&gt;1&lt;/span&gt;)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    plt.pcolormesh(x1, x2, y_hat, cmap=cm_light, shading=&lt;span class=&#34;string&#34;&gt;&amp;#x27;auto&amp;#x27;&lt;/span&gt;)  &lt;span class=&#34;comment&#34;&gt;# 预测值&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    plt.scatter(x_train[pair[&lt;span class=&#34;number&#34;&gt;0&lt;/span&gt;]],&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                x_train[pair[&lt;span class=&#34;number&#34;&gt;1&lt;/span&gt;]],&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                c=y_train,&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                s=&lt;span class=&#34;number&#34;&gt;20&lt;/span&gt;,&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                edgecolors=&lt;span class=&#34;string&#34;&gt;&amp;#x27;k&amp;#x27;&lt;/span&gt;,&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                cmap=cm_dark,&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                label=&lt;span class=&#34;string&#34;&gt;u&amp;#x27;训练集&amp;#x27;&lt;/span&gt;)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    plt.scatter(x_test[pair[&lt;span class=&#34;number&#34;&gt;0&lt;/span&gt;]],&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                x_test[pair[&lt;span class=&#34;number&#34;&gt;1&lt;/span&gt;]],&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                c=y_test,&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                s=&lt;span class=&#34;number&#34;&gt;80&lt;/span&gt;,&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                marker=&lt;span class=&#34;string&#34;&gt;&amp;#x27;*&amp;#x27;&lt;/span&gt;,&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                edgecolors=&lt;span class=&#34;string&#34;&gt;&amp;#x27;k&amp;#x27;&lt;/span&gt;,&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                cmap=cm_dark,&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                label=&lt;span class=&#34;string&#34;&gt;u&amp;#x27;测试集&amp;#x27;&lt;/span&gt;)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    plt.xlabel(iris_feature[pair[&lt;span class=&#34;number&#34;&gt;0&lt;/span&gt;]], fontsize=&lt;span class=&#34;number&#34;&gt;12&lt;/span&gt;)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    plt.ylabel(iris_feature[pair[&lt;span class=&#34;number&#34;&gt;1&lt;/span&gt;]], fontsize=&lt;span class=&#34;number&#34;&gt;12&lt;/span&gt;)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    &lt;span class=&#34;comment&#34;&gt;# plt.legend(loc=&amp;#x27;upper right&amp;#x27;, fancybox=True, framealpha=0.3)&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    plt.xlim(x1_min, x1_max)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    plt.ylim(x2_min, x2_max)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    plt.grid(b=&lt;span class=&#34;literal&#34;&gt;True&lt;/