&lt;h2 id=&#34;基本问题&#34;&gt;基本问题&lt;/h2&gt;
&lt;h3 id=&#34;回归问题&#34;&gt;回归问题&lt;/h3&gt;
&lt;h4 id=&#34;线性回归&#34;&gt;线性回归&lt;/h4&gt;
&lt;figure class=&#34;highlight python&#34;&gt;&lt;table&gt;&lt;tr&gt;&lt;td class=&#34;code&#34;&gt;&lt;pre&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;keyword&#34;&gt;import&lt;/span&gt; numpy &lt;span class=&#34;keyword&#34;&gt;as&lt;/span&gt; np&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;keyword&#34;&gt;import&lt;/span&gt; matplotlib.pyplot &lt;span class=&#34;keyword&#34;&gt;as&lt;/span&gt; mp&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;keyword&#34;&gt;from&lt;/span&gt; mpl_toolkits.mplot3d &lt;span class=&#34;keyword&#34;&gt;import&lt;/span&gt; axes3d&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;keyword&#34;&gt;import&lt;/span&gt; sklearn.preprocessing &lt;span class=&#34;keyword&#34;&gt;as&lt;/span&gt; sp&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;comment&#34;&gt;# 训练数据集&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;train_x = np.array([&lt;span class=&#34;number&#34;&gt;0.5&lt;/span&gt;, &lt;span class=&#34;number&#34;&gt;0.6&lt;/span&gt;, &lt;span class=&#34;number&#34;&gt;0.8&lt;/span&gt;, &lt;span class=&#34;number&#34;&gt;1.1&lt;/span&gt;, &lt;span class=&#34;number&#34;&gt;1.4&lt;/span&gt;])  &lt;span class=&#34;comment&#34;&gt;# 输入集&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;train_y = np.array([&lt;span class=&#34;number&#34;&gt;5.0&lt;/span&gt;, &lt;span class=&#34;number&#34;&gt;5.5&lt;/span&gt;, &lt;span class=&#34;number&#34;&gt;6.0&lt;/span&gt;, &lt;span class=&#34;number&#34;&gt;6.8&lt;/span&gt;, &lt;span class=&#34;number&#34;&gt;7.0&lt;/span&gt;])  &lt;span class=&#34;comment&#34;&gt;# 输出集&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;n_epochs = &lt;span class=&#34;number&#34;&gt;30&lt;/span&gt;  &lt;span class=&#34;comment&#34;&gt;# 迭代次数&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;l_rate = &lt;span class=&#34;number&#34;&gt;0.01&lt;/span&gt;  &lt;span class=&#34;comment&#34;&gt;# 学习率&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;epochs = []  &lt;span class=&#34;comment&#34;&gt;# 记录迭代次数&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;losses = []  &lt;span class=&#34;comment&#34;&gt;# 记录损失值&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;w0, w1 = [&lt;span class=&#34;number&#34;&gt;1&lt;/span&gt;], [&lt;span class=&#34;number&#34;&gt;1&lt;/span&gt;]  &lt;span class=&#34;comment&#34;&gt;# 模型初始值&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;keyword&#34;&gt;for&lt;/span&gt; i &lt;span class=&#34;keyword&#34;&gt;in&lt;/span&gt; &lt;span class=&#34;built_in&#34;&gt;range&lt;/span&gt;(&lt;span class=&#34;number&#34;&gt;1&lt;/span&gt;, n_epochs + &lt;span class=&#34;number&#34;&gt;1&lt;/span&gt;):&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    epochs.append(i)  &lt;span class=&#34;comment&#34;&gt;# 记录第几次迭代&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    y = w0[-&lt;span class=&#34;number&#34;&gt;1&lt;/span&gt;] + w1[-&lt;span class=&#34;number&#34;&gt;1&lt;/span&gt;] * train_x  &lt;span class=&#34;comment&#34;&gt;# 取出最新的w0,w1计算线性方程输出&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    &lt;span class=&#34;comment&#34;&gt;# 损失函数(均方差)&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    loss = (((train_y - y)**&lt;span class=&#34;number&#34;&gt;2&lt;/span&gt;).&lt;span class=&#34;built_in&#34;&gt;sum&lt;/span&gt;()) / &lt;span class=&#34;number&#34;&gt;2&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    losses.append(loss)  &lt;span class=&#34;comment&#34;&gt;# 记录每次迭代的损失值&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    &lt;span class=&#34;built_in&#34;&gt;print&lt;/span&gt;(&lt;span class=&#34;string&#34;&gt;&amp;quot;%d: w0=%f, w1=%f, loss=%f&amp;quot;&lt;/span&gt; % (i, w0[-&lt;span class=&#34;number&#34;&gt;1&lt;/span&gt;], w1[-&lt;span class=&#34;number&#34;&gt;1&lt;/span&gt;], loss))&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    &lt;span class=&#34;comment&#34;&gt;# 计算w0,w1的偏导数&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    d0 = -(train_y - y).&lt;span class=&#34;built_in&#34;&gt;sum&lt;/span&gt;()&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    d1 = -(train_x * (train_y - y)).&lt;span class=&#34;built_in&#34;&gt;sum&lt;/span&gt;()&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    &lt;span class=&#34;comment&#34;&gt;# 更新w0,w1&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    w0.append(w0[-&lt;span class=&#34;number&#34;&gt;1&lt;/span&gt;] - (d0 * l_rate))&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    w1.append(w1[-&lt;span class=&#34;number&#34;&gt;1&lt;/span&gt;] - (d1 * l_rate))&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;comment&#34;&gt;###################### 训练过程可视化 ######################&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;comment&#34;&gt;## 损失函数收敛过程&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;w0 = np.array(w0[:-&lt;span class=&#34;number&#34;&gt;1&lt;/span&gt;])&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;w1 = np.array(w1[:-&lt;span class=&#34;number&#34;&gt;1&lt;/span&gt;])&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;mp.figure(&lt;span class=&#34;string&#34;&gt;&amp;quot;Losses&amp;quot;&lt;/span&gt;, facecolor=&lt;span class=&#34;string&#34;&gt;&amp;quot;lightgray&amp;quot;&lt;/span&gt;)  &lt;span class=&#34;comment&#34;&gt;# 创建一个窗体&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;mp.title(&lt;span class=&#34;string&#34;&gt;&amp;quot;epoch&amp;quot;&lt;/span&gt;, fontsize=&lt;span class=&#34;number&#34;&gt;20&lt;/span&gt;)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;mp.ylabel(&lt;span class=&#34;string&#34;&gt;&amp;quot;loss&amp;quot;&lt;/span&gt;, fontsize=&lt;span class=&#34;number&#34;&gt;14&lt;/span&gt;)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;mp.grid(linestyle=&lt;span class=&#34;string&#34;&gt;&amp;quot;:&amp;quot;&lt;/span&gt;)  &lt;span class=&#34;comment&#34;&gt;# 网格线：虚线&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;mp.plot(epochs, losses, c=&lt;span class=&#34;string&#34;&gt;&amp;quot;blue&amp;quot;&lt;/span&gt;, label=&lt;span class=&#34;string&#34;&gt;&amp;quot;loss&amp;quot;&lt;/span&gt;)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;mp.legend()  &lt;span class=&#34;comment&#34;&gt;# 图例&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;mp.tight_layout()  &lt;span class=&#34;comment&#34;&gt;# 紧凑格式&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;comment&#34;&gt;## 显示模型直线&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;pred_y = w0[-&lt;span class=&#34;number&#34;&gt;1&lt;/span&gt;] + w1[-&lt;span class=&#34;number&#34;&gt;1&lt;/span&gt;] * train_x  &lt;span class=&#34;comment&#34;&gt;# 根据x预测y&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;mp.figure(&lt;span class=&#34;string&#34;&gt;&amp;quot;Linear Regression&amp;quot;&lt;/span&gt;, facecolor=&lt;span class=&#34;string&#34;&gt;&amp;quot;lightgray&amp;quot;&lt;/span&gt;)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;mp.title(&lt;span class=&#34;string&#34;&gt;&amp;quot;Linear Regression&amp;quot;&lt;/span&gt;, fontsize=&lt;span class=&#34;number&#34;&gt;20&lt;/span&gt;)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;mp.xlabel(&lt;span class=&#34;string&#34;&gt;&amp;quot;x&amp;quot;&lt;/span&gt;, fontsize=&lt;span class=&#34;number&#34;&gt;14&lt;/span&gt;)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;mp.ylabel(&lt;span class=&#34;string&#34;&gt;&amp;quot;y&amp;quot;&lt;/span&gt;, fontsize=&lt;span class=&#34;number&#34;&gt;14&lt;/span&gt;)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;mp.grid(linestyle=&lt;span class=&#34;string&#34;&gt;&amp;quot;:&amp;quot;&lt;/span&gt;)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;mp.scatter(train_x, train_y, c=&lt;span class=&#34;string&#34;&gt;&amp;quot;blue&amp;quot;&lt;/span&gt;, label=&lt;span class=&#34;string&#34;&gt;&amp;quot;Traing&amp;quot;&lt;/span&gt;)  &lt;span class=&#34;comment&#34;&gt;# 绘制样本散点图&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;mp.plot(train_x, pred_y, c=&lt;span class=&#34;string&#34;&gt;&amp;quot;red&amp;quot;&lt;/span&gt;, label=&lt;span class=&#34;string&#34;&gt;&amp;quot;Regression&amp;quot;&lt;/span&gt;)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;mp.legend()&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;comment&#34;&gt;# 显示梯度下降过程(复制粘贴即可，不需要编写)&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;comment&#34;&gt;# 计算损失函数曲面上的点 loss = f(w0, w1)&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;arr1 = np.linspace(&lt;span class=&#34;number&#34;&gt;0&lt;/span&gt;, &lt;span class=&#34;number&#34;&gt;10&lt;/span&gt;, &lt;span class=&#34;number&#34;&gt;500&lt;/span&gt;)  &lt;span class=&#34;comment&#34;&gt;# 0~9间产生500个元素的均匀列表&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;arr2 = np.linspace(&lt;span class=&#34;number&#34;&gt;0&lt;/span&gt;, &lt;span class=&#34;number&#34;&gt;3.5&lt;/span&gt;, &lt;span class=&#34;number&#34;&gt;500&lt;/span&gt;)  &lt;span class=&#34;comment&#34;&gt;# 0~3.5间产生500个元素的均匀列表&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;grid_w0, grid_w1 = np.meshgrid(arr1, arr2)  &lt;span class=&#34;comment&#34;&gt;# 产生二维矩阵&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;flat_w0, flat_w1 = grid_w0.ravel(), grid_w1.ravel()  &lt;span class=&#34;comment&#34;&gt;# 二维矩阵扁平化&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;loss_metrix = train_y.reshape(-&lt;span class=&#34;number&#34;&gt;1&lt;/span&gt;, &lt;span class=&#34;number&#34;&gt;1&lt;/span&gt;)  &lt;span class=&#34;comment&#34;&gt;# 生成误差矩阵（-1,1）表示自动计算维度&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;outer = np.outer(train_x, flat_w1)  &lt;span class=&#34;comment&#34;&gt;# 求外积（train_x和flat_w1元素两两相乘的新矩阵）&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;comment&#34;&gt;# 计算损失：((w0 + w1*x - y)**2)/2&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;flat_loss = (((flat_w0 + outer - loss_metrix)**&lt;span class=&#34;number&#34;&gt;2&lt;/span&gt;).&lt;span class=&#34;built_in&#34;&gt;sum&lt;/span&gt;(axis=&lt;span class=&#34;number&#34;&gt;0&lt;/span&gt;)) / &lt;span class=&#34;number&#34;&gt;2&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;grid_loss = flat_loss.reshape(grid_w0.shape)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;mp.figure(&lt;span class=&#34;string&#34;&gt;&amp;#x27;Loss Function&amp;#x27;&lt;/span&gt;)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;ax = mp.axes(projection=&lt;span class=&#34;string&#34;&gt;&amp;#x27;3d&amp;#x27;&lt;/span&gt;)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;mp.title(&lt;span class=&#34;string&#34;&gt;&amp;#x27;Loss Function&amp;#x27;&lt;/span&gt;, fontsize=&lt;span class=&#34;number&#34;&gt;14&lt;/span&gt;)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;ax.set_xlabel(&lt;span class=&#34;string&#34;&gt;&amp;#x27;w0&amp;#x27;&lt;/span&gt;, fontsize=&lt;span class=&#34;number&#34;&gt;14&lt;/span&gt;)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;ax.set_ylabel(&lt;span class=&#34;string&#34;&gt;&amp;#x27;w1&amp;#x27;&lt;/span&gt;, fontsize=&lt;span class=&#34;number&#34;&gt;14&lt;/span&gt;)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;ax.set_zlabel(&lt;span class=&#34;string&#34;&gt;&amp;#x27;loss&amp;#x27;&lt;/span&gt;, fontsize=&lt;span class=&#34;number&#34;&gt;14&lt;/span&gt;)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;ax.plot_surface(grid_w0,&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                grid_w1,&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                grid_loss,&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                rstride=&lt;span class=&#34;number&#34;&gt;10&lt;/span&gt;,&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                cstride=&lt;span class=&#34;number&#34;&gt;10&lt;/span&gt;,&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                cmap=&lt;span class=&#34;string&#34;&gt;&amp;#x27;jet&amp;#x27;&lt;/span&gt;)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;ax.plot(w0, w1, losses, &lt;span class=&#34;string&#34;&gt;&amp;#x27;o-&amp;#x27;&lt;/span&gt;, c=&lt;span class=&#34;string&#34;&gt;&amp;#x27;orangered&amp;#x27;&lt;/span&gt;, label=&lt;span class=&#34;string&#34;&gt;&amp;#x27;BGD&amp;#x27;&lt;/span&gt;, zorder=&lt;span class=&#34;number&#34;&gt;5&lt;/span&gt;)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;mp.legend(loc=&lt;span class=&#34;string&#34;&gt;&amp;#x27;lower left&amp;#x27;&lt;/span&gt;)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;mp.show()&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;/figure&gt;
