&lt;li&gt;&lt;a href=&#34;#%E7%A1%AC%E7%9B%98%E6%A0%BC%E5%BC%8F%E7%AE%80%E4%BB%8B&#34;&gt;硬盘格式简介&lt;/a&gt;&lt;/li&gt;
&lt;li&gt;&lt;a href=&#34;#%E4%B8%A4%E7%A7%8D%E7%A1%AC%E7%9B%98%E6%A0%BC%E5%BC%8F&#34;&gt;两种硬盘格式&lt;/a&gt;
&lt;ul&gt;
&lt;li&gt;&lt;a href=&#34;#gpt-%E6%A0%BC%E5%BC%8F&#34;&gt;GPT 格式&lt;/a&gt;&lt;/li&gt;
&lt;li&gt;&lt;a href=&#34;#mbr-%E6%A0%BC%E5%BC%8F&#34;&gt;MBR 格式&lt;/a&gt;&lt;/li&gt;
&lt;/ul&gt;
&lt;/li&gt;
&lt;li&gt;&lt;a href=&#34;#%E4%B8%8A%E6%9C%BA%E6%93%8D%E4%BD%9C&#34;&gt;上机操作&lt;/a&gt;&lt;/li&gt;
