  10 &amp;amp; 1000 &amp;amp; 100\\
  01 &amp;amp; 0100 &amp;amp; 010\\
  10 &amp;amp; 0010 &amp;amp; 001\\
  01 &amp;amp; 0001 &amp;amp; 100\\
\end{bmatrix}
