w^0
  &amp;amp;= 1 \\
w^1
  &amp;amp;= w^0 - \alpha \cdot J&amp;#x27;(w^0) \\
  &amp;amp;= 1 - 0.4 \cdot 2 \\
  &amp;amp;= 0.2 \\
w^2
  &amp;amp;= w^1 - \alpha \cdot J&amp;#x27;(w^1) \\
  &amp;amp;= 0.04 \\
w^3
  &amp;amp;= 0.008 \\
w^4
  &amp;amp;= 0.0016 \\
