;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;curl_setopt ( &lt;span class=&#34;variable&#34;&gt;$ch&lt;/span&gt;, CURLOPT_CONNECTTIMEOUT, &lt;span class=&#34;number&#34;&gt;10&lt;/span&gt; );&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;curl_setopt ( &lt;span class=&#34;variable&#34;&gt;$ch&lt;/span&gt;, CURLOPT_POST, &lt;span class=&#34;number&#34;&gt;1&lt;/span&gt; ); &lt;span class=&#34;regexp&#34;&gt;//&lt;/span&gt;启用POST提交&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;variable&#34;&gt;$file_contents&lt;/span&gt; = curl_exec ( &lt;span class=&#34;variable&#34;&gt;$ch&lt;/span&gt; );&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;curl_close ( &lt;span class=&#34;variable&#34;&gt;$ch&lt;/span&gt; );&lt;/span&gt;&lt;br&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;/figure&gt;
