&lt;figure class=&#34;highlight python&#34;&gt;&lt;table&gt;&lt;tr&gt;&lt;td class=&#34;gutter&#34;&gt;&lt;pre&gt;&lt;span class=&#34;line&#34;&gt;1&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;2&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;3&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;4&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;5&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;6&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;7&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;8&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;9&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;10&lt;/span&gt;&lt;br&gt;&lt;/pre&gt;&lt;/td&gt;&lt;td class=&#34;code&#34;&gt;&lt;pre&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;keyword&#34;&gt;from&lt;/span&gt; skl2onnx &lt;span class=&#34;keyword&#34;&gt;import&lt;/span&gt; convert_sklearn&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;keyword&#34;&gt;from&lt;/span&gt; skl2onnx.common.data_types &lt;span class=&#34;keyword&#34;&gt;import&lt;/span&gt; FloatTensorType&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;comment&#34;&gt;# å¼ é‡ä¸º 380&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;initial_type = [(&lt;span class=&#34;string&#34;&gt;&amp;#x27;float_input&amp;#x27;&lt;/span&gt;, FloatTensorType([&lt;span class=&#34;literal&#34;&gt;None&lt;/span&gt;, &lt;span class=&#34;number&#34;&gt;380&lt;/span&gt;]))]&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;options = &amp;#123;&lt;span class=&#34;built_in&#34;&gt;id&lt;/span&gt;(model): &amp;#123;&lt;span class=&#34;string&#34;&gt;&amp;#x27;nocl&amp;#x27;&lt;/span&gt;: &lt;span class=&#34;literal&#34;&gt;True&lt;/span&gt;, &lt;span class=&#34;string&#34;&gt;&amp;#x27;zipmap&amp;#x27;&lt;/span&gt;: &lt;span class=&#34;literal&#34;&gt;False&lt;/span&gt;&amp;#125;&am