6&lt;/span&gt;  bytes &lt;span class=&#34;number&#34;&gt;37199464&lt;/span&gt; (&lt;span class=&#34;number&#34;&gt;37&lt;/span&gt;.&lt;span class=&#34;number&#34;&gt;1&lt;/span&gt; MB)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        &lt;span class=&#34;attribute&#34;&gt;TX&lt;/span&gt; errors &lt;span class=&#34;number&#34;&gt;0&lt;/span&gt;  dropped &lt;span class=&#34;number&#34;&gt;0&lt;/span&gt; overruns &lt;span class=&#34;number&#34;&gt;0&lt;/span&gt;  carrier &lt;span class=&#34;number&#34;&gt;0&lt;/span&gt;  collisions &lt;span class=&#34;number&#34;&gt;0&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;attribute&#34;&gt;lo&lt;/span&gt;: flags=&lt;span class=&#34;number&#34;&gt;73&lt;/span&gt;&amp;lt;UP,LOOPBACK,RUNNING&amp;gt;  mtu &lt;span class=&#34;number&#34;&gt;65536&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        &lt;span class=&#34;attribute&#34;&gt;inet&lt;/span&gt; &lt;span class=&#34;number&#34;&gt;127.0.0.1&lt;/span&gt;  netmask &lt;span class=&#34;number&#34;&gt;255.0.0.0&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        &lt;span class=&#34;attribute&#34;&gt;inet6&lt;/span&gt; ::&lt;span class=&#34;number&#34;&gt;1&lt;/span&gt;  prefixlen &lt;span class=&#34;number&#34;&gt;128&lt;/span&gt;  scopeid &lt;span class=&#34;number&#34;&gt;0&lt;/span&gt;x&lt;span class=&#34;number&#34;&gt;10&lt;/span&gt;&amp;lt;host&amp;gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        &lt;span class=&#34;attribute&#34;&gt;loop&lt;/span&gt;  txqueuelen &lt;span class=&#34;number&#34;&gt;1000&lt;/span&gt;  (Local Loopback)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        &lt;span class=&#34;attribute&#34;&gt;RX&lt;/span&gt; packets &lt;span class=&#34;number&#34;&gt;14643&lt;/span&gt;  bytes &lt;span class=&#34;number&#34;&gt;53874531&lt;/span&gt; (&lt;span class=&#34;number&#34