&lt;figure class=&#34;highlight dockerfile&#34;&gt;&lt;table&gt;&lt;tr&gt;&lt;td class=&#34;code&#34;&gt;&lt;pre&gt;&lt;span class=&#34;line&#34;&gt;reg &lt;span class=&#34;keyword&#34;&gt;add&lt;/span&gt;&lt;span class=&#34;language-bash&#34;&gt; &lt;span class=&#34;string&#34;&gt;&amp;quot;HKEY_LOCAL_MACHINE\SYSTEM\CurrentControlSet\Control\Session Manager\Kernel&amp;quot;&lt;/span&gt; /v DisableTsx /t REG_DWORD /d 0 /f&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;/figure&gt;
