&lt;figure class=&#34;highlight taggerscript&#34;&gt;&lt;table&gt;&lt;tr&gt;&lt;td class=&#34;gutter&#34;&gt;&lt;pre&gt;&lt;span class=&#34;line&#34;&gt;1&lt;/span&gt;&lt;br&gt;&lt;/pre&gt;&lt;/td&gt;&lt;td class=&#34;code&#34;&gt;&lt;pre&gt;&lt;span class=&#34;line&#34;&gt;mklink /d &amp;quot;C:&lt;span class=&#34;symbol&#34;&gt;\U&lt;/span&gt;sers&lt;span class=&#34;symbol&#34;&gt;\2&lt;/span&gt;9845&lt;span class=&#34;symbol&#34;&gt;\A&lt;/span&gt;ppData&lt;span class=&#34;symbol&#34;&gt;\R&lt;/span&gt;oaming&lt;span class=&#34;symbol&#34;&gt;\u&lt;/span&gt;Tools&amp;quot; &amp;quot;D:&lt;span class=&#34;symbol&#34;&gt;\G&lt;/span&gt;ame&lt;span class=&#34;symbol&#34;&gt;\S&lt;/span&gt;coop&lt;span class=&#34;symbol&#34;&gt;\p&lt;/span&gt;ersist&lt;span class=&#34;symbol&#34;&gt;\u&lt;/span&gt;Tools&amp;quot;&lt;/span&gt;&lt;br&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;/figure&gt;
