&lt;/ul&gt;
&lt;figure class=&#34;highlight coq&#34;&gt;&lt;table&gt;&lt;tr&gt;&lt;td class=&#34;gutter&#34;&gt;&lt;pre&gt;&lt;span class=&#34;line&#34;&gt;1&lt;/span&gt;&lt;br&gt;&lt;/pre&gt;&lt;/td&gt;&lt;td class=&#34;code&#34;&gt;&lt;pre&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;keyword&#34;&gt;Import&lt;/span&gt;-&lt;span class=&#34;keyword&#34;&gt;Module&lt;/span&gt; PoShFuck&lt;/span&gt;&lt;br&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;/figure&gt;
&lt;hr&gt;
&lt;/li&gt;
&lt;li&gt;
&lt;p&gt;&lt;code&gt;PSReadline&lt;/code&gt;&lt;/p&gt;
&lt;figure class=&#34;highlight mathematica&#34;&gt;&lt;table&gt;&lt;tr&gt;&lt;td class=&#34;gutter&#34;&gt;&lt;pre&gt;&lt;span class=&#34;line&#34;&gt;1&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;2&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;3&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;4&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;5&lt;/span&gt;&lt;br&gt;&lt;/pre&gt;&lt;/td&gt;&lt;td class=&#34;code&#34;&gt;&lt;pre&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;type&#34;&gt;#&lt;/span&gt; &lt;span class=&#34;variable&#34;&gt;Enable&lt;/span&gt; &lt;span class=&#34;variable&#34;&gt;Prediction&lt;/span&gt; &lt;span class=&#34;variable&#34;&gt;History&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;built_in&#34;&gt;Set&lt;/span&gt;&lt;span class=&#34;operator&#34;&gt;-&lt;/span&gt;&lt;span class=&#34;variable&#34;&gt;PSReadLineOption&lt;/span&gt; &lt;span class=&#34;operator&#34;&gt;-&lt;/span&gt;&lt;span class=&#34;variable&#34;&gt;PredictionSource&lt;/span&gt; &lt;span class=&#34;variable&#34;&gt;History&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;type&#34;&gt;##&lt;/span&gt; &lt;span class=&#34;variable&#34;&gt;Advanced&lt;/span&gt; &lt;span class=&#34;variable&#34;&gt;Autocompletion&lt;/span&gt; &lt;span class=&#34;variable&#34;&gt;for&lt;/span&gt; &lt;span class=&#34;variable&#34;&gt;arrow&lt;/span&gt; &lt;span class=&#34;variable&#34;&gt;keys&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;built_in&#34;&gt;Set&lt;/span&gt;&lt;span class=&#34;operator&#34;&gt;-&lt;/span&gt;&lt;span class=&#34;variable&#34;&gt;PSReadlineKeyHandler&lt;/span&gt; &lt;span class=&#34;operator&#34;&gt;-&lt;/span&gt;&lt;span class=&#34;built_in&#34;&gt;Key&lt;/span&gt; &lt;span class=&#34;built_in&#34;&gt;UpArrow&lt;/span&gt; &lt;span class=&#34;operator&#34;&gt;-&lt;/span&gt;&lt;span class=&#34;built_in&#34;&gt;Function&lt;/span&gt; &lt;span class=&#34;variable&#34;&gt;HistorySearchBackward&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;built_in&#34;&gt;Set&lt;/span&gt;&lt;span class=&#34;operator&#34;&gt;-&lt;/span&gt;&lt;span class=&#34;variable&#34;&gt;PSReadlineKeyHandler&lt;/span&gt; &lt;span class=&#34;operator&#34;&gt;-&lt;/span&gt;&lt;span class=&#34;built_in&#34;&gt;Key&lt;/span&gt; &lt;span class=&#34;built_in&#34;&gt;DownArrow&lt;/span&gt; &lt;span class=&#34;operator&#34;&gt;-&lt;/span&gt;&lt;span class=&#34;built_in&#34;&gt;Function&lt;/span&gt; &lt;span class=&#34;variable&#34;&gt;HistorySearchForward&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;/figure&gt;
