卡内余额 = 余额 - 取的钱&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    account.balance = account.balance.subtract(drawingMoney);&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    System.out.println(&lt;span class=&#34;built_in&#34;&gt;this&lt;/span&gt;.getName() + &lt;span class=&#34;string&#34;&gt;&amp;quot;取走&amp;quot;&lt;/span&gt; + drawingMoney);&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    &lt;span class=&#34;comment&#34;&gt;//手里的钱&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    nowMoney = nowMoney.add(drawingMoney);&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    System.out.println(account.name + &lt;span class=&#34;string&#34;&gt;&amp;quot;余额为：&amp;quot;&lt;/span&gt; + account.balance);&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    System.out.println(&lt;span class=&#34;built_in&#34;&gt;this&lt;/span&gt;.getName() + &lt;span class=&#34;string&#34;&gt;&amp;quot;手里的钱：&amp;quot;&lt;/span&gt; + nowMoney);&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;  &amp;#125;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&amp;#125;&lt;/span&gt;&lt;br&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;/figure&gt;
