&lt;h3 id=&#34;smb-内网文件共享&#34;&gt;smb-内网文件共享&lt;/h3&gt;
&lt;h4 id=&#34;安装配置&#34;&gt;安装配置&lt;/h4&gt;
&lt;figure class=&#34;highlight shell&#34;&gt;&lt;table&gt;&lt;tr&gt;&lt;td class=&#34;code&#34;&gt;&lt;pre&gt;&lt;span class=&#34;line&#34;&gt;sudo apt update &amp;amp;&amp;amp; sudo apt install exfat-fuse exfatprogs ntfs-3g samba samba-common-bin&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;dpkg -l | grep -E &amp;quot;exfat|ntfs&amp;quot;&lt;/span&gt;&lt;br&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;/figure&gt;
