&lt;figure class=&#34;highlight apache&#34;&gt;&lt;table&gt;&lt;tr&gt;&lt;td class=&#34;code&#34;&gt;&lt;pre&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;attribute&#34;&gt;UUID&lt;/span&gt;=&lt;span class=&#34;number&#34;&gt;749&lt;/span&gt;C-&lt;span class=&#34;number&#34;&gt;1&lt;/span&gt;C4F /mnt/storage exfat defaults,auto,users,rw,nofail,uid=&lt;span class=&#34;number&#34;&gt;1000&lt;/span&gt;,gid=&lt;span class=&#34;number&#34;&gt;1000&lt;/span&gt; &lt;span class=&#34;number&#34;&gt;0&lt;/span&gt; &lt;span class=&#34;number&#34;&gt;0&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;attribute&#34;&gt;UUID&lt;/span&gt;=&lt;span class=&#34;number&#34;&gt;026815&lt;/span&gt;AC5FEFC0F7 /mnt/stack ntfs defaults,auto,users,rw,nofail,uid=&lt;span class=&#34;number&#34;&gt;1000&lt;/span&gt;,gid=&lt;span class=&#34;number&#34;&gt;1000&lt;/span&gt; &lt;span class=&#34;number&#34;&gt;0&lt;/span&gt; &lt;span class=&#34;number&#34;&gt;0&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;/figure&gt;
&lt;/li&gt;
